#include &lt;stdlib.h&gt;
#include &lt;unistd.h&gt;

typedef struct
{
	int count;
	sem_t sem;
} shared;

shared *shmem;
int shmid;
key_t shmkey;

int main()
{
    int alive=1;

    shmkey = ftok(&quot;makefile&quot;,777);
    if (shmkey == -1) {
	    perror(&quot;Ftok failed&quot;);
	    exit(-1);
    }

    // Get our shm id
    shmid = shmget(shmkey, sizeof *shmem, 0);
    if (shmid == -1) {
        perror(&quot;Failed to allocate shared memory region&quot;);
        exit(-1);
    }

    // Attach to our shared memory
    shmem = (shared*)shmat(shmid, (void *)0, 0);
    if (shmem == (shared *)-1) {
	    perror(&quot;Failed to attach to shared memory region&quot;);
	    exit(-1);
    }

    do
    {
        sem_wait(&amp;(shmem-&gt;sem));
        printf(&quot;%d\n&quot;, shmem-&gt;count);
        if(shmem-&gt;count &gt; 5)
        {
            shmem-&gt;count=0; 
            alive=0;
        }
        sem_post(&amp;(shmem-&gt;sem));
