    def encode(s):
      result, prev = [], &#39;&#39;
    
      for v in s:
        if not prev or prev[-1] == v:
          # continue current pattern
          prev += v
        else:
          # pattern change
          result.append(prev)
          prev = v
    
      if prev:
        result.append(prev)
      return result
    
    a = &quot;00001&quot;
    b = &quot;00011&quot; 
