      &lt;metadata&gt;
        &lt;id&gt;WpfCustomControlLibrary1&lt;/id&gt;
        &lt;version&gt;1.0.0&lt;/version&gt;
        &lt;authors&gt;WpfCustomControlLibrary1&lt;/authors&gt;
