                mexPrintf(&quot;\n data is %d\n&quot;, V[j]);//int
            else if constexpr (std::is_same_v&lt;typename T::value_type, double&gt;)
                mexPrintf(&quot;\n data is %g\n&quot;, V[j]);//double
