strsep is trivial to implement yourself

    #include &lt;string.h&gt;

    char *strsep(char **stringp, const char *delim) {
        char *rv = *stringp;
        if (rv) {
            *stringp += strcspn(*stringp, delim);
            if (**stringp)
                *(*stringp)++ = &#39;\0&#39;;
            else
                *stringp = 0; }
        return rv;
    }

