 var rowContents=sheet.getRange(lastRow,1,1,sheet.getLastColumn()).getValues();
 SpreadsheetApp.openById(secodarySheetId).getSheetByName(&quot;Sheet1&quot;).appendRow(rowContents[0]);
}
