    $formatted_meta_data = $item-&gt;get_formatted_meta_data();
    // create an array of just the keys
    $keys = array_column($formatted_meta_data, &#39;key&#39;);
    // create an array of just the values
    $values = array_column($formatted_meta_data, &#39;value&#39;);
    // combine the arrays into a more useful format
    $names = array_combine($keys, $values);

