    async function startNewStream( device ) {
      // request a stream from a particular device
      const deviceType = device.kind.replace( &#39;input&#39;, &#39;&#39; );
      const constraints = {
        [ deviceType ]: { deviceId: device.id }
      }
      const stream = await navigator.mediaDevices.getUserMedia( constraints );
      // do whatever you wish with this stream now
