int lr_guid_gen()
{
    char GUID[40];
    int t = 0;
    char *szTemp = &quot;xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx&quot;;
    char *szHex = &quot;0123456789abcdef-&quot;;
    int nLen = strlen (szTemp);
    
    
    for (t=0; t&lt;nLen+1; t++)
    {
        int r = rand () % 16;
        char c = &#39; &#39;;   
    
        switch (szTemp[t])
        {
            case &#39;x&#39; : { c = szHex [r]; } break;
