    &lt;!-- ============================= START CDMWeb BUILD =================================== 	--&gt;	
        &lt;property environment=&quot;env&quot; /&gt;
        
        &lt;import file=&quot;resource/apache-beehive-1.0.2\beehive-imports.xml&quot;/&gt;
        &lt;import file=&quot;resource/apache-beehive-1.0.2\ant\beehive-tools.xml&quot;/&gt;
        
