        function authSendEmail($from, $namefrom, $to, $nameto, $subject, $headers, $message) {


	$smtpServer = &quot;mail.yourhostname.com&quot;;
	$port = &quot;25&quot;; //Or whatever the default port on your server is
	$timeout = &quot;30&quot;;
	$username = &quot;origin@yourdomain.com&quot;;
	$password = &quot;myemailpassword&quot;;
	$localhost = &quot;yourhostname.com&quot;;
	$newLine = &quot;\r\n&quot;;
	$date = date(&#39;r&#39;, time()); 

 
        //Connect to the host on the specified port
	$smtpConnect = fsockopen($smtpServer, $port, $errno, $errstr, $timeout);
	$smtpResponse = fgets($smtpConnect, 515);
	if(empty($smtpConnect))	{
		$output = &quot;Failed to connect: $smtpResponse&quot;;
		return $output;
	} else {
		$logArray[&#39;connection&#39;] = &quot;Connected: $smtpResponse&quot;;
	}
 
       //Request Auth Login
	fputs($smtpConnect,&quot;AUTH LOGIN&quot; . $newLine);
	$smtpResponse = fgets($smtpConnect, 515);
	$logArray[&#39;authrequest&#39;] = &quot;$smtpResponse&quot;;
 
        //Send username
	fputs($smtpConnect, base64_encode($username) . $newLine);
	$smtpResponse = fgets($smtpConnect, 515);
	$logArray[&#39;authusername&#39;] = &quot;$smtpResponse&quot;;
 
        //Send password
	fputs($smtpConnect, base64_encode($password) . $newLine);
	$smtpResponse = fgets($smtpConnect, 515);
	$logArray[&#39;authpassword&#39;] = &quot;$smtpResponse&quot;;
 
        //Say Hello to SMTP
	fputs($smtpConnect, &quot;HELO $localhost&quot; . $newLine);
	$smtpResponse = fgets($smtpConnect, 515);
	$logArray[&#39;heloresponse&#39;] = &quot;$smtpResponse&quot;;
 
        //Email From
	fputs($smtpConnect, &quot;MAIL FROM: $from&quot; . $newLine);
	$smtpResponse = fgets($smtpConnect, 515);
	$logArray[&#39;mailfromresponse&#39;] = &quot;$smtpResponse&quot;;
 
        //Email To
	fputs($smtpConnect, &quot;RCPT TO: $to&quot; . $newLine);
	$smtpResponse = fgets($smtpConnect, 515);
	$logArray[&#39;mailtoresponse&#39;] = &quot;$smtpResponse&quot;;
 
        //The Email
	fputs($smtpConnect, &quot;DATA&quot; . $newLine);
	$smtpResponse = fgets($smtpConnect, 515);
	$logArray[&#39;data1response&#39;] = &quot;$smtpResponse&quot;;
 
    fputs($smtpConnect, &quot;To: $to\nFrom: $from\nSubject: $subject\n$headers\n\n$message\n.\n&quot;);
	$smtpResponse = fgets($smtpConnect, 515);
	$logArray[&#39;data2response&#39;] = &quot;$smtpResponse&quot;;
 
         // Say Bye to SMTP
	fputs($smtpConnect,&quot;QUIT&quot; . $newLine);
	$smtpResponse = fgets($smtpConnect, 515);
	$logArray[&#39;quitresponse&#39;] = &quot;$smtpResponse&quot;;
 
    //Show log
    //	echo &quot;&lt;pre&gt;$to&lt;br/&gt;&quot;;
    //	print_r ($logArray);
    //	echo &quot;&lt;/pre&gt;&lt;hr /&gt;&quot;;
     	$output = $logArray;
    	return $output;
    }	
 

And then you can call it like so:

