```
        val filteredList = list.filter { it.item?.doesContainQuery(query) ?: true }
        val results = filteredList.toMutableList()

        filteredList.forEachIndexed { index, itemWithSection -&gt;
            val nextItem = filteredList.getOrNull(index + 1) ?: return@forEachIndexed

            if (itemWithSection.isSectionEmpty(nextItem)) {
                results.remove(itemWithSection)
            }
        }
        return results
