    function has(object, path) {
        return path.split(&#39;.&#39;).every(k =&gt; object.hasOwnProperty(k) &amp;&amp; (object = object[k]));
    }

    function hasMultipleKeys(object, keys) {
        return keys.every(has.bind(null, object));
