&lt;!-- language: lang-js --&gt;

    class Ball {
      constructor(x,y,xSpeed,ySpeed,r){
        this.x = x;
        this.y = y;
        this.xSpeed = xSpeed;
        this.ySpeed = ySpeed;
        this.r = r;
      }

      draw(){
        ctx.clearRect(0,0, 500, 200);
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.r, 0, 2 * Math.PI);
        ctx.stroke();
      }

      move(){
        this.x += this.xSpeed;
        this.y += this.ySpeed;
        if (this.x == 0) {
          this.xSpeed = 2;
        } else if(this.x == 500) {
          this.xSpeed =- 2;
        }
                    
        if (this.y == 200) {
          this.ySpeed =- 1;
        } else if (this.y == 0) {
          this.ySpeed = 1;
        }
        window.requestAnimationFrame(this.move.bind(this));
      }
    }

    var canvas = document.getElementById(&quot;canvas-for-ball&quot;);
    var ctx = canvas.getContext(&quot;2d&quot;)
    let balls = new Ball(10,10,2,1,5);
    balls.draw();
    balls.move();

