    def Compare(X, Y):
        X=str(X)   # Convert to string in case integer parameters are passed (if you are
        Y=str(Y)   # sure that only string will be passed you can skip these two lines)
        
        XY = X+Y
        YX = Y+X
        
        if (XY &gt; YX):
