    function tripleThreat(arr) {
       
        if (arr.find((v,i) =&gt; 
                arr.length &gt; i+2 
                &amp;&amp; v + 1 === arr[i+1] 
                &amp;&amp; v + 2 === arr[i+2]) === undefined) {
            return 0;
        }
        return 1;
    }

    console.log(tripleThreat([3,1,2,3]));
