fun&lt;T&gt; combine(context: AppCompatActivity, vararg input: LiveData&lt;T&gt;): LiveData&lt;T&gt; {
    val output = MutableLiveData&lt;T&gt;()
    input.forEach {
        it.observe(context, androidx.lifecycle.Observer { value -&gt; 
            output.value = value
        })
    }
    return output
