    	   COUNT(P2.NodeName) WITHIN GROUP (GRAPH PATH) AS [Depth],
           STRING_AGG(P2.NodeName,&#39;-&gt;&#39;) WITHIN GROUP (GRAPH PATH) AS [Assembly],
