    	   LAST_VALUE(P2.ProductName) WITHIN GROUP (GRAPH PATH) AS [Final Product Name],
    	   LAST_VALUE(P2.SupplierName) WITHIN GROUP (GRAPH PATH) AS [Final Supplier Name]
