      coloured_div.id = &quot;coloured_div&quot; + i;
      document.getElementById(&quot;body&quot;).appendChild(coloured_div);

      coloured_div.addEventListener(&quot;mouseover&quot;, hoverCube);
    }

    function hoverCube(e) {
      e.target.style.backgroundColor = &quot;orange&quot;;
    }

&lt;!-- language: lang-css --&gt;

