&lt;xsl:stylesheet version=&quot;3.0&quot; 
    xmlns:xsl=&quot;http://www.w3.org/1999/XSL/Transform&quot;
    xmlns=&quot;http://www.w3.org/2005/xpath-functions&quot;&gt;
