&lt;!-- language: lang-js --&gt;

    const app = new Vue({
      el: &quot;#app&quot;,
      data: function() {
        return {
          emotion: &quot;&quot;,
          intensity: null,
          newIntensity: null,
          emotionIntensity: []
        };
      },
      methods: {
        openEmotion(id) {
          this.emotionId = id;
        },
        addEmotionIntensity() {
          var emotionIntensity = {
            emotion: this.emotion,
            intensity: this.intensity,
            newIntensity: this.intensity
          };
          this.emotionIntensity.push(emotionIntensity);
          this.emotion = &quot;&quot;;
          this.intensity = null;
        },
        removeEmotion(id) {
          if (id &gt; -1) {
            this.emotionIntensity.splice(id, 1);
          }
        },
      }
    });

