       &lt;option value=&quot;15&quot;&gt; 42&lt;/option&gt;
    &lt;/select&gt;
    &lt;span&gt;select 2&lt;/span&gt;
    &lt;select class=&quot;form-control show_data2&quot; id=&quot;tip_rola2&quot; name=&quot;tip_rola2&quot;&gt;
