#include &lt;memory&gt;
#include &lt;string&gt;

struct node {
    std::string name;
    int rollno;
    std::unique_ptr&lt;node&gt; next;
};

void create(std::unique_ptr&lt;node&gt; &amp;first)
{
    std::unique_ptr&lt;node&gt; newnode = std::make_unique&lt;node&gt;();
    std::cout &lt;&lt; &quot;--------------------------\n&quot;;
    std::cout &lt;&lt; &quot;Enter Student Information\n&quot;;
    std::cout &lt;&lt; &quot;Name- \t&quot;;
    std::cin &gt;&gt; newnode-&gt;name;
    std::cout &lt;&lt; &quot;Roll No- &quot;;
    std::cin &gt;&gt; newnode-&gt;rollno;
    std::cout &lt;&lt; &quot;--------------------------\n&quot;;
    if (!first)
        first = std::move(newnode);
    else {
        auto a = first.get();
        while (a-&gt;next)
            a = a-&gt;next.get();
        a-&gt;next = std::move(newnode);
    }
}

void display(const std::unique_ptr&lt;node&gt; &amp;first)
{
    if (!first)
        std::cout &lt;&lt; &quot;Empty List\n&quot;;
    else {
        auto temp = first.get();
        while (temp) {
            std::cout &lt;&lt; &quot;Student Information\n&quot;;
            std::cout &lt;&lt; &quot;--------------------------\n&quot;;
            std::cout &lt;&lt; &quot;Name- &quot;;
            std::cout &lt;&lt; temp-&gt;name;
            std::cout &lt;&lt; &quot;\n&quot;;
            std::cout &lt;&lt; &quot;Roll No- &quot;;
            std::cout &lt;&lt; temp-&gt;rollno;
            std::cout &lt;&lt; &quot;\n&quot;;
            std::cout &lt;&lt; &quot;--------------------------\n&quot;;
            temp = temp-&gt;next.get();
        }
    }
}

int main()
{
    std::unique_ptr&lt;node&gt; first;
