    	{
    		if ( i == curr_size )
    		{
    			char *tmp = realloc( ptr, curr_size * FACTOR );
    			
    			if ( tmp == NULL ) break;
    			
    			curr_size *= FACTOR;
    			ptr = tmp;
    		}
    
    		ptr[i] = c;
    	}
    	
