    if(sessionStorage.getItem(&#39;alertShown&#39;) === null){
      window.setTimeout(function() {
          $(&quot;.alert-credit, .alert-info&quot;).fadeTo(1000, 0).slideUp(1000, function(){
              sessionStorage.setItem(&#39;alertShown&#39;, &#39;true&#39;);
              $(this).remove(); 
          });   
      }, 9000);
    }else{
    	$(&quot;.alert-credit, .alert-info&quot;).remove();
    }

