 5. FInally your code for dynamically creating link with little code change.
 6. The entire code snippet is below

 
 





        async function getBase64ImageFromUrl(imageUrl) {
            var res = await fetch(imageUrl);
            var blob = await res.blob();
    
            return new Promise((resolve, reject) =&gt; {
              var reader  = new FileReader();
              reader.addEventListener(&quot;load&quot;, function () {
                      resolve(reader.result);
                  }, false);
    
              reader.onerror = () =&gt; {
                 return reject(this);
              };
              reader.readAsDataURL(blob);
           })
        }
    
    
        getBase64ImageFromUrl(&#39;https://cors-anywhere.herokuapp.com/&#39;+&#39;https://tinyjpg.com/images/social/website.jpg&#39;)
        .then(function(result) {
    
    		var link = document.createElement(&#39;a&#39;)
    		link.href = result
    		link.style.display = &#39;none&#39;;
    		link.setAttribute(&#39;download&#39;,&#39;&#39;);
    		document.body.appendChild(link);
    		link.click();
    		document.body.removeChild(link)}
    	)
        .catch(err =&gt; console.error(err));


