```js
import { useRouter } from &#39;next/router&#39;

export default function ActiveLink({ children, href }) {
  const router = useRouter()
  const style = {
    marginRight: 10,
    color: router.pathname === href ? &#39;red&#39; : &#39;black&#39;,
  }

  const handleClick = e =&gt; {
    e.preventDefault()
    router.push(href)
  }

  return (
    &lt;a href={href} onClick={handleClick} style={style}&gt;
      {children}
    &lt;/a&gt;
  )
}
```

