    public static boolean isPrime(int n, ArrayList&lt;Integer&gt; primes) {

        if (n == 2) {
            return true;
        }

        int stop = (int)Math.sqrt(n);

        for (int divisor : primes) {
            if ((n % divisor) == 0)  {
                return false;
            }
            if (divisor &gt; stop) {
                break;
            }
        }

