&lt;!-- begin snippet: js hide: false console: true babel: true --&gt;

&lt;!-- language: lang-js --&gt;

    class Foo extends React.Component {
      constructor(props) {
        super(props);
        this.state = {
          filterSearchQuery: &quot;&quot;,
          filterSearch: &quot;&quot;
        };

        this.handleSearchInput = this.handleSearchInput.bind(this);
        this.handleSearchReset = this.handleSearchReset.bind(this);
      }

      handleSearchInput(e) {
        if (e.keyCode === 13) {
          const {filterSearchQuery} = this.state;
          this.setState({
            filterSearchQuery: &quot;&quot;,
            filterSearch: filterSearchQuery,
          });
        }
        else {
          this.setState({
            filterSearchQuery: e.target.value
          });
        }
      }

      handleSearchReset(e) {
        this.setState({
          filterSearchQuery: &quot;&quot;,
          filterSearch: &quot;&quot;
        });
      }

      render() {
        const {filterSearchQuery, filterSearch} = this.state;
        return (
          &lt;div&gt;
            &lt;input 
              value={filterSearchQuery} 
              onKeyUp={this.handleSearchInput} 
              onChange={this.handleSearchInput} 
            /&gt;
            {filterSearchQuery &amp;&amp;
              &lt;div&gt;
                &lt;button onClick={this.handleSearchReset}&gt;
                  &lt;span&gt;&amp;times;&lt;/span&gt;
                &lt;/button&gt;
              &lt;/div&gt;
            }
            {filterSearch &amp;&amp;
              &lt;div&gt;
                ...testing search for &#39;{filterSearch}&#39;...
              &lt;/div&gt;
            }
          &lt;/div&gt;
        );
      }
    }

    ReactDOM.render(&lt;Foo /&gt;, document.getElementById(&quot;root&quot;));

&lt;!-- language: lang-html --&gt;

