var
  XML : IXMLDocument;
  RootNode, CurNode : IXMLNode;
begin
  XML := NewXMLDocument;
  XML.Encoding := &#39;utf-8&#39;;
  XML.Options := [doNodeAutoIndent]; // looks better in Editor ;)

  RootNode := XML.AddChild(&#39;p:test&#39;);
  RootNode.DeclareNamespace(&#39;ds&#39;, &#39;Attributes1&#39;);
  RootNode.DeclareNamespace(&#39;p&#39;, &#39;Attributes2&#39;);
