    $(&#39;.nav-menu-list ul li&#39;).hover(function() {
      let id = &quot;#&quot; + $(this).data(&quot;id&quot;)
      $(id).fadeIn();
    }, function() {
      let id = &quot;#&quot; + $(this).data(&quot;id&quot;)
      $(id).fadeOut();
    });

