			key[i].p = static_cast&lt;long&gt;(i);
			frac = static_cast&lt;float&gt;(rand()) / static_cast&lt;float&gt;(RAND_MAX);
			sequence[i] = static_cast&lt;int&gt;(frac  * static_cast&lt;float&gt;(NN));
