		// part to check and improve
		// =========================================Random=======================================================
		std::chrono::high_resolution_clock::time_point TstartMain = std::chrono::high_resolution_clock::now();
		double tmp;
		long rnk;
		
		for(int j=0; j &lt; 1000; j++)
