			ii = sequence[i];
			tmp = key[ii].x[1];
			rnk = key[ii].p;
			key[ii].x[1] = tmp * 1.01;
			key[ii].p = rnk * 1.01;
		}
		
		
		std::chrono::high_resolution_clock::time_point TendMain = std::chrono::high_resolution_clock::now();
