function onChange() {
  var ss=SpreadsheetApp.getActive();
  var importSheet=ss.getSheetByName(&quot;Sheet1&quot;);
  var finalSheet=ss.getSheetByName(&quot;Sheet2&quot;);
  var importRange=importSheet.getDataRange();
