class Solution {
    public boolean search(int[] groups, int row, int[] nums, int target) {
        if (row &lt; 0) return true;
        int v = nums[row--];
        for (int i = 0; i &lt; groups.length; i++) {
            if (groups[i] + v &lt;= target) {
                groups[i] += v;
