      const [count, dispatch] = React.useReducer(reducer, 0);

      const asyncInc = React.useCallback(() =&gt; {
        setTimeout(() =&gt; dispatch(2), 1000); 
      }, [dispatch]);

      return (
        &lt;div onClick={asyncInc}&gt;{count}&lt;/div&gt;
