    let s = 0;
    for (var i = 0; i &lt; matrix.length; i++) {
        for (var j = 0; j &lt; matrix[0].length; j++) {
            if (matrix[i][j] == 0 &amp;&amp; i+1 &lt; matrix.length) {
                matrix[i+1][j] = 0;
            }
            s += matrix[i][j]
        }
    }
    return s
}
