    s = &#39;222910222&#39;

    s.each_char.each_cons(2).drop_while { |a, b| a == b }.map(&amp;:last).join
    #=&gt; &quot;910222&quot;

    # or

    s.each_char.drop_while.with_index(-1) { |c, i| i &lt; 0 || c == s[i] }.join
    #=&gt; &quot;910222&quot;


