    Console.WriteLine(&quot;All done&quot;);
    Console.ReadLine();
}


public class MyCls
{
    static MyCls()
    {
        for (int i = 0; i &lt; 10; i++)
        {
            if (i == 4)
            {
                Console.WriteLine($&quot;sctor calling Abort on itself&quot;);
