Use reduce

      var arrayOfObjects = [
      { value1: 50, },
      { value2: 100, value3: 600 },
      { value4: 500, value5: 700 }
    ];

    const sum = arrayOfObjects.reduce((total, current) =&gt; {
        const values = Object.values(current);
        return values.length &gt; 1 ? values.reduce((last, next) =&gt; last + next, 0) + total : total
    }, 0)

