#&gt; $data_structure_a
#&gt; [1] &quot;column_names/col_id&quot;    &quot;column_names/col_value&quot;
#&gt; 
#&gt; $data_structure_b
#&gt; [1] &quot;column_names/col_value&quot; &quot;column_names/col_id&quot;
