		this.scopeFactory = scopeFactory;
		this.logger = logger;
	}

	public Task StartAsync(CancellationToken stoppingToken)
	{
		timer = new Timer(DoWork, null, TimeSpan.Zero, TimeSpan.FromSeconds(10));

		return Task.CompletedTask;
	}

