export function filter&lt;T, K extends keyof T&gt;(object: T, ...keys: Array&lt;K&gt;): Omit&lt;T, K&gt; {
  let index = -1;
  const length = keys.length;
  const result = { ...object };

  while (++index &lt; length) {
    const key = keys[index];
    if (key in object) delete result[key];
  }
