      ip: &quot;13.21.219.253&quot;
      ports:
      - 80
      - 443
    - host: test-test.net
      ip: &quot;13.71.225.255&quot;
      ports:
      - 3306

`templates/headless.yaml`- this one create for each host a clusterIP service with a single endpoint. a little overwhelming but it just works.

    {{ range .Values.headless }}
    ---
    kind: Service
    apiVersion: v1
    metadata:
     name: {{ .host }}
     labels:
    {{ include &quot;external-hosts.labels&quot; $ | indent 4 }}
    spec:
     ports:
     {{ range .ports }}
     - name: {{ . | quote }}
       port: {{ . }}
       targetPort: {{ . }}
    {{ end }}
    {{ end }}
    ---
    
    {{ range .Values.headless }}
    ---
    kind: Endpoints
    apiVersion: v1
    metadata:
     name: {{ .host }}
     labels:
    {{ include &quot;external-hosts.labels&quot; $ | indent 4 }}
    subsets:
     - addresses:
         - ip: {{ .ip }}
       ports:
       {{ range .ports }}
         - name: {{ . | quote}}
           port: {{ . }}
      {{ end }}
