&lt;!-- language: lang-css --&gt;

    html {
      margin: 0px;
      padding: 0px;
    }

    header {
      position: fixed;
      top: 0;
      left: 0;
      width: 100vw;
      background: #e8e8e8;
    }

    #header-img {
      border: solid 1px;
    }

    .container {
      display: flex;
      border: solid 1px;
    }

    ul {
      flex: 1;
      /* this */
      list-style: none;
      display: flex;
      justify-content: space-around;
    }

    li {
      flex: 1;
    }

&lt;!-- language: lang-html --&gt;

    &lt;header&gt;
      &lt;nav class=&quot;container&quot;&gt;
        &lt;img id=&quot;header-img&quot; src=&quot;https://www.dropbox.com/s/09cucxl9sq8stvt/LogoMakr_7edr0A.png?raw=1&quot;&gt;
        &lt;ul&gt;
