    select v.value, v.cnt
    from (select value, count(*) as cnt,
                 row_number() over (order by count(*) desc) as seqnum_desc,
                 row_number() over (order by count(*) asc) as seqnum_asc
          from t
          group by value
         ) v
    where seqnum_desc &lt;= 2 or seqnum_asc &lt;= 2;

