    char * filter( const char *p, char *q )
    {
    	const char *vowels = &quot;aeiou&quot;;
    	char *result = q;
    	
    	do
    	{
    		if ( *p == &#39;\0&#39; || strchr( vowels, tolower( ( unsigned char )*p ) ) == NULL ) 
