You are pretty much at the optimum solution. I implemented @bserdar&#39;s suggestion of sorting first and ran a benchmark against it.


&gt; Note: this is an extremely crude implementation. Take it with a pound of salt. 

Package and import omitted for readability.

```go
var slice = []int{2, 5, 6, 7, 8, 2, 4, 1, 1, 1, 2, -2, -2, 2, 2, 3, -1, 0, 0, 0, 0, 2, 5, 4, 9, 8, 7, 2, -3, -7}

func orig(s []int) (negative, zero, positive int) {
	for _, v := range s {
		if v &gt; 0 {
			positive++
		} else if v &lt; 0 {
			negative++
		} else if v == 0 {
			zero++
		}
	}
	return
}

func sorted(s []int) (negative, zero, positive int) {
	// We do not want to modify the input slice,
	// so we need to create a copy of it
	sortedSlice := make([]int, len(s))
	copy(sortedSlice, s)
	sort.Ints(sortedSlice)
	return preSorted(sortedSlice)
}

func preSorted(s []int) (int, int, int) {
	var z, p int
	var zfound bool
	for i := 0; i &lt; len(s); i++ {
		if s[i] &lt; 0 {
			continue
		} else if !zfound &amp;&amp; s[i] == 0 {
			zfound = true
			z = i
		} else if s[i] &gt; 0 {
			p = i
			break
		}
	}
	return z, p - z, len(s) - p
}
```

The test code:

```go
func BenchmarkOrig(b *testing.B) {
	for i := 0; i &lt; b.N; i++ {
		orig(slice)
	}
}

func BenchmarkLongOrig(b *testing.B) {
	var slice = make([]int, 10000000)
	for i := 0; i &lt; 10000000; i++ {
		slice[i] = rand.Intn(10)
		if rand.Intn(2) == 0 {
			slice[i] = slice[i] * -1
		}
	}
	b.ResetTimer()
	for i := 0; i &lt; b.N; i++ {
		orig(slice)
	}
}
func BenchmarkSorted(b *testing.B) {
	for i := 0; i &lt; b.N; i++ {
		sorted(slice)
	}
}

func BenchmarkLongSorted(b *testing.B) {
	var slice = make([]int, 10000000)
	for i := 0; i &lt; 10000000; i++ {
		slice[i] = rand.Intn(10)
		if rand.Intn(2) == 0 {
			slice[i] = slice[i] * -1
		}
	}
	b.ResetTimer()
	for i := 0; i &lt; b.N; i++ {
		sorted(slice)
	}
}

func BenchmarkPresorted(b *testing.B) {
	cp := make([]int, len(slice))
	copy(cp, slice)
	sort.Ints(cp)
	b.ResetTimer()
	for i := 0; i &lt; b.N; i++ {
		preSorted(cp)
	}
}

func BenchmarkLongPresorted(b *testing.B) {
	var slice = make([]int, 10000000)
	for i := 0; i &lt; 10000000; i++ {
		slice[i] = rand.Intn(10)
		if rand.Intn(2) == 0 {
			slice[i] = slice[i] * -1
		}
	}
	sort.Ints(slice)
	b.ResetTimer()
	for i := 0; i &lt; b.N; i++ {
		sorted(slice)
	}
}
```


